import React, { Component } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Platform,
  TouchableHighlight,
  FlatList
} from 'react-native';
import SSC from 'sscjs';

class App extends Component {
  state = {
    holders: [],
    holders_data: []
  }

  onClick = () => {
    const ssc = new SSC('https://api.steem-engine.com/rpc/');
    ssc.stream((err, res) => {
        // console.log(err, res);
    });
    ssc.find('tokens', 'balances', {'symbol':'JJM' }, 1000, 0, [], (err, result) => {
      for (var holder of result) {
        this.state.holders.push(holder)
        var text = holder.account+'-'+holder.balance
        this.state.holders_data.push({key: text})
      }
      this.setState({holders, holders_data});
    })


  }

  render() {

    return (
      <View style={styles.container}>
        <TouchableHighlight
          onPress={this.onClick}
          style={styles.button}
          underlayColor={'#0A84D0'}
        >
          <Text style={styles.buttonText}>Get JJM List</Text>
        </TouchableHighlight>
        <FlatList
          data= {this.state.holders_data}
          renderItem={({item}) => <Text style={styles.item}>{item.key}</Text>}
        />

      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 300,
    height: 300,
  },
  title: {
    fontWeight: 'bold',
    fontSize: 16,
  },
  button: {
    borderRadius: 3,
    padding: 20,
    marginVertical: 10,
    marginTop: 10,
    backgroundColor: '#1B95E0',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 16,
  },
  item: {
    padding: 10,
    fontSize: 18,
    height: 44,
  },
});

let hotWrapper = () => () => App;
if (Platform.OS === 'web') {
  const { hot } = require('react-hot-loader');
  hotWrapper = hot;
}
export default hotWrapper(module)(App);
